// LivingRoomPage.js

import React, { useState } from 'react';

const LivingRoomPage = () => {
  // State variables for controlling each item
  const [ceilingLight, setCeilingLight] = useState(false);
  const [readingLamp, setReadingLamp] = useState(false);
  const [indirectLight, setIndirectLight] = useState(false);
  const [sockets, setSockets] = useState(false);

  return (
    <div>
      <h2>Living Room</h2>
      <div className="card">
        <h3>Controlling:</h3>
        <div className="form-check">
          <input
            type="checkbox"
            className="form-check-input"
            id="ceilingLightToggle"
            checked={ceilingLight}
            onChange={() => setCeilingLight(!ceilingLight)}
          />
          <label className="form-check-label" htmlFor="ceilingLightToggle">
            Ceiling light
          </label>
        </div>
        <div className="form-check">
          <input
            type="checkbox"
            className="form-check-input"
            id="readingLampToggle"
            checked={readingLamp}
            onChange={() => setReadingLamp(!readingLamp)}
          />
          <label className="form-check-label" htmlFor="readingLampToggle">
            Reading lamp
          </label>
        </div>
        <div className="form-check">
          <input
            type="checkbox"
            className="form-check-input"
            id="indirectLightToggle"
            checked={indirectLight}
            onChange={() => setIndirectLight(!indirectLight)}
          />
          <label className="form-check-label" htmlFor="indirectLightToggle">
            Indirect light
          </label>
        </div>
        <div className="form-check">
          <input
            type="checkbox"
            className="form-check-input"
            id="socketsToggle"
            checked={sockets}
            onChange={() => setSockets(!sockets)}
          />
          <label className="form-check-label" htmlFor="socketsToggle">
            Sockets
          </label>
        </div>
      </div>
    </div>
  );
};

export default LivingRoomPage;
