// export default Navbar;

import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const Navbar = () => {
  const [currentDateTime, setCurrentDateTime] = useState(new Date());

  useEffect(() => {
    const intervalId = setInterval(() => {
      setCurrentDateTime(new Date());
    }, 1000); // Update every second

    return () => clearInterval(intervalId); // Cleanup on unmount
  }, []);

  const formattedDateTime = currentDateTime.toLocaleString('en-GB', {
    weekday: 'long', // Full day of the week (e.g., Monday)
    day: 'numeric', // Numeric day (e.g., 11)
    month: 'long', // Full month name (e.g., March)
    year: 'numeric', // Full year (e.g., 2024)
    hour: 'numeric', // 24-hour format (e.g., 10)
    minute: 'numeric', // Minutes (e.g., 41)
    second: 'numeric', // Seconds (e.g., 55)
    hour12: false // Use 24-hour format
  });

  const style = { color: 'goldenrod' }; // Inline style for goldenrod color

  return (
    <nav className="navbar navbar-expand-lg bg-body-tertiary">
      <div className="container-fluid">
        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarSupportedContent">
          <ul className="navbar-nav me-auto mb-2 mb-lg-0">
            <li className="nav-item">
              <Link className="nav-link active" aria-current="page" to="/">Home</Link>
            </li>
            <li className="nav-item dropdown">
              <a className="nav-link dropdown-toggle" href="#" role="button" data-bs-toggle="dropdown" aria-expanded="false">
                Select a room
              </a>
              <ul className="dropdown-menu">
                <li><Link className="dropdown-item" to="/living-room">Living Room</Link></li>
                <li><Link className="dropdown-item" to="/bedroom">Bedroom</Link></li>
                <li><Link className="dropdown-item" to="/kitchen">Kitchen</Link></li>
                <li><Link className="dropdown-item" to="/bathroom">Bathroom</Link></li>
                <li><Link className="dropdown-item" to="/office">Office</Link></li>
                <li><Link className="dropdown-item" to="/dining">Dining Room</Link></li>
              </ul>
            </li>
          </ul>
          <div className="navbar-text" style={style}>
            {formattedDateTime}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
