// LivingRoomPage.js

import React, { useState } from 'react';

const LivingRoomPage = () => {
  // State variables for controlling each item
  const [ceilingLight, setCeilingLight] = useState(false);
  const [readingLamp, setReadingLamp] = useState(false);
  const [indirectLight, setIndirectLight] = useState(false);
  const [sockets, setSockets] = useState(false);

  // Function to toggle the state of each item
  const toggleItem = (item) => {
    switch (item) {
      case 'ceilingLight':
        setCeilingLight(!ceilingLight);
        break;
      case 'readingLamp':
        setReadingLamp(!readingLamp);
        break;
      case 'indirectLight':
        setIndirectLight(!indirectLight);
        break;
      case 'sockets':
        setSockets(!sockets);
        break;
      default:
        break;
    }
  };

  return (
    <div className="container">
      <h1>VFlorida Smart Home</h1>
      <ul className="device-list">
        <li className="device-item">
          <span>Living Room Lights</span>
          <button className="toggle-button" style={{ backgroundColor: ceilingLight ? '#4caf50' : '#ff6347' }} onClick={() => toggleItem('ceilingLight')}>
            {ceilingLight ? 'On' : 'Off'}
          </button>
        </li>
        <li className="device-item">
          <span>Reading Lamp</span>
          <button className="toggle-button" style={{ backgroundColor: readingLamp ? '#4caf50' : '#ff6347' }} onClick={() => toggleItem('readingLamp')}>
            {readingLamp ? 'On' : 'Off'}
          </button>
        </li>
        <li className="device-item">
          <span>Indirect Light</span>
          <button className="toggle-button" style={{ backgroundColor: indirectLight ? '#4caf50' : '#ff6347' }} onClick={() => toggleItem('indirectLight')}>
            {indirectLight ? 'On' : 'Off'}
          </button>
        </li>
        <li className="device-item">
          <span>Sockets</span>
          <button className="toggle-button" style={{ backgroundColor: sockets ? '#4caf50' : '#ff6347' }} onClick={() => toggleItem('sockets')}>
            {sockets ? 'On' : 'Off'}
          </button>
        </li>
      </ul>
    </div>
  );
};

export default LivingRoomPage;
