import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faLightbulb } from '@fortawesome/free-solid-svg-icons';

const LivingRoomPage = () => {
  const [lightsOn, setLightsOn] = useState(false);

  const toggleLights = () => {
    setLightsOn(!lightsOn);
  };

  const buttonWidth = lightsOn ? 'auto' : 'min-content';

  return (
    <div className="container">
      <h1 className="text-center">Lights Control</h1>
      <hr style={{ borderTop: '3px solid goldenrod' }} />
      <h2 className="text-center">Living Room</h2>
      <hr style={{ borderTop: '1px solid #ccc' }} />
      <ul className="device-list">
        <li className="device-item">
          <FontAwesomeIcon
            icon={faLightbulb}
            style={{ marginRight: '10px', color: lightsOn ? 'goldenrod' : 'dark' }}
          />
          <span className="label" style={{ flexBasis: '50%' }}>Living Room Lights</span>
          <button
            className="toggle-button"
            style={{ flexBasis: '20%', minWidth: buttonWidth, backgroundColor: lightsOn ? 'red' : 'green' }}
            onClick={toggleLights}
          >
            <span style={{ textDecoration: lightsOn ? 'none' : 'underline', color: 'white' }}>
              {lightsOn ? 'Off' : 'On'}
            </span>
          </button>
        </li>
        {/* Additional devices can be added here */}
      </ul>
    </div>
  );
};

export default LivingRoomPage;
