// LivingRoomPage.js

import React, { useState } from 'react';

const LivingRoomPage = () => {
  // State variables for controlling each item
  const [ceilingLight, setCeilingLight] = useState(false);
  const [readingLamp, setReadingLamp] = useState(false);
  const [indirectLight, setIndirectLight] = useState(false);
  const [sockets, setSockets] = useState(false);

  // Function to toggle the state of each item
  const toggleItem = (item) => {
    switch (item) {
      case 'ceilingLight':
        setCeilingLight(!ceilingLight);
        break;
      case 'readingLamp':
        setReadingLamp(!readingLamp);
        break;
      case 'indirectLight':
        setIndirectLight(!indirectLight);
        break;
      case 'sockets':
        setSockets(!sockets);
        break;
      default:
        break;
    }
  };

  return (
    <div>
      <h2>Living Room</h2>
      <div className="card">
        <h3>Controlling:</h3>
        <button className={`btn btn-${ceilingLight ? 'success' : 'danger'}`} onClick={() => toggleItem('ceilingLight')}>
          {ceilingLight ? 'Ceiling light ON' : 'Ceiling light OFF'}
        </button>
        <button className={`btn btn-${readingLamp ? 'success' : 'danger'}`} onClick={() => toggleItem('readingLamp')}>
          {readingLamp ? 'Reading lamp ON' : 'Reading lamp OFF'}
        </button>
        <button className={`btn btn-${indirectLight ? 'success' : 'danger'}`} onClick={() => toggleItem('indirectLight')}>
          {indirectLight ? 'Indirect light ON' : 'Indirect light OFF'}
        </button>
        <button className={`btn btn-${sockets ? 'success' : 'danger'}`} onClick={() => toggleItem('sockets')}>
          {sockets ? 'Sockets ON' : 'Sockets OFF'}
        </button>
      </div>
    </div>
  );
};

export default LivingRoomPage;
